// Transform Garmin data to garmin_daily_stats table format
const dailyData = $('Get Garmin Daily Summary').first().json;
const sleepData = $('Get Sleep Data').first().json;

const today = new Date().toISOString().split('T')[0];

// Parse data according to actual DB schema
const daily = {
  date: today,

  // Sleep metrics (minutes, not seconds)
  sleep_score: sleepData.sleepScores?.overall?.value || null,
  sleep_duration_minutes: Math.round((sleepData.sleepTimeSeconds || 0) / 60),
  deep_sleep_minutes: Math.round((sleepData.deepSleepSeconds || 0) / 60),
  light_sleep_minutes: Math.round((sleepData.lightSleepSeconds || 0) / 60),
  rem_sleep_minutes: Math.round((sleepData.remSleepSeconds || 0) / 60),
  awake_minutes: Math.round((sleepData.awakeSleepSeconds || 0) / 60),

  // Body Battery
  body_battery_start: dailyData.bodyBatteryLowestValue || null,
  body_battery_end: dailyData.bodyBatteryMostRecentValue || null,
  body_battery_charged: null, // Calculate if data available
  body_battery_drained: null, // Calculate if data available

  // Stress
  stress_avg: dailyData.averageStressLevel || null,
  stress_max: dailyData.maxStressLevel || null,
  rest_stress_minutes: null,
  low_stress_minutes: null,
  medium_stress_minutes: null,
  high_stress_minutes: null,

  // Activity
  steps: dailyData.totalSteps || null,
  active_calories: dailyData.activeKilocalories || null,
  total_calories: dailyData.totalKilocalories || null,
  intensity_minutes: (dailyData.moderateIntensityMinutes || 0) + (dailyData.vigorousIntensityMinutes || 0),
  floors_climbed: dailyData.floorsAscended || null,

  // Heart
  resting_hr: dailyData.restingHeartRate || null,

  // HRV
  hrv_status: dailyData.hrvStatus || null,
  hrv_value: dailyData.hrvLastNight || null,

  synced_at: new Date().toISOString()
};

return [{ json: daily }];

// Analyze data and check for alerts
const data = $input.first().json;
const alerts = [];

// Sleep quality check
const sleepHours = data.sleep_duration_seconds / 3600;
if (sleepHours < 6) {
  alerts.push({
    type: 'low_sleep',
    severity: 'warning',
    message: `Nur ${sleepHours.toFixed(1)}h Schlaf letzte Nacht`
  });
}

// Body Battery check
if (data.body_battery_current < 30) {
  alerts.push({
    type: 'low_battery',
    severity: 'warning',
    message: `Body Battery nur ${data.body_battery_current}%`
  });
}

// Stress check
if (data.average_stress > 50) {
  alerts.push({
    type: 'high_stress',
    severity: 'info',
    message: `Durchschnittlicher Stress: ${data.average_stress}`
  });
}

// HRV check
if (data.hrv_status === 'LOW' || data.hrv_status === 'POOR') {
  alerts.push({
    type: 'low_hrv',
    severity: 'warning',
    message: `HRV Status: ${data.hrv_status}`
  });
}

// Resting HR check (if unusual)
if (data.resting_hr > 80) {
  alerts.push({
    type: 'high_rhr',
    severity: 'info',
    message: `Ruhepuls erhÃ¶ht: ${data.resting_hr} bpm`
  });
}

return [{
  json: {
    ...data,
    alerts,
    has_alerts: alerts.length > 0
  }
}];
