// Transform Garmin data to our format
const dailyData = $('Get Garmin Daily Summary').first().json;
const sleepData = $('Get Sleep Data').first().json;

const today = new Date().toISOString().split('T')[0];

// Parse daily summary
const daily = {
  date: today,

  // Activity
  steps: dailyData.totalSteps || 0,
  distance_meters: dailyData.totalDistanceMeters || 0,
  active_calories: dailyData.activeKilocalories || 0,
  total_calories: dailyData.totalKilocalories || 0,
  floors_climbed: dailyData.floorsAscended || 0,
  active_minutes: dailyData.moderateIntensityMinutes + dailyData.vigorousIntensityMinutes || 0,

  // Stress & Energy
  average_stress: dailyData.averageStressLevel || 0,
  max_stress: dailyData.maxStressLevel || 0,
  body_battery_high: dailyData.bodyBatteryHighestValue || 0,
  body_battery_low: dailyData.bodyBatteryLowestValue || 0,
  body_battery_current: dailyData.bodyBatteryMostRecentValue || 0,

  // Heart
  resting_hr: dailyData.restingHeartRate || 0,
  max_hr: dailyData.maxHeartRate || 0,
  min_hr: dailyData.minHeartRate || 0,
  avg_hr: dailyData.averageHeartRate || 0,

  // HRV
  hrv_weekly_avg: dailyData.hrvWeeklyAverage || null,
  hrv_last_night: dailyData.hrvLastNight || null,
  hrv_status: dailyData.hrvStatus || null,

  // Sleep (from sleep endpoint)
  sleep_start: sleepData.sleepStartTimestampGMT || null,
  sleep_end: sleepData.sleepEndTimestampGMT || null,
  sleep_duration_seconds: sleepData.sleepTimeSeconds || 0,
  sleep_score: sleepData.sleepScores?.overall?.value || 0,
  deep_sleep_seconds: sleepData.deepSleepSeconds || 0,
  light_sleep_seconds: sleepData.lightSleepSeconds || 0,
  rem_sleep_seconds: sleepData.remSleepSeconds || 0,
  awake_seconds: sleepData.awakeSleepSeconds || 0,

  // Respiration
  avg_respiration: dailyData.averageRespirationValue || null,

  // SpO2
  avg_spo2: dailyData.averageOxygenSaturation || null,

  // Raw data for reference
  raw_daily: dailyData,
  raw_sleep: sleepData
};

return [{ json: daily }];

// Analyze data and check for alerts
const data = $input.first().json;
const alerts = [];

// Sleep quality check
const sleepHours = data.sleep_duration_seconds / 3600;
if (sleepHours < 6) {
  alerts.push({
    type: 'low_sleep',
    severity: 'warning',
    message: `Nur ${sleepHours.toFixed(1)}h Schlaf letzte Nacht`
  });
}

// Body Battery check
if (data.body_battery_current < 30) {
  alerts.push({
    type: 'low_battery',
    severity: 'warning',
    message: `Body Battery nur ${data.body_battery_current}%`
  });
}

// Stress check
if (data.average_stress > 50) {
  alerts.push({
    type: 'high_stress',
    severity: 'info',
    message: `Durchschnittlicher Stress: ${data.average_stress}`
  });
}

// HRV check
if (data.hrv_status === 'LOW' || data.hrv_status === 'POOR') {
  alerts.push({
    type: 'low_hrv',
    severity: 'warning',
    message: `HRV Status: ${data.hrv_status}`
  });
}

// Resting HR check (if unusual)
if (data.resting_hr > 80) {
  alerts.push({
    type: 'high_rhr',
    severity: 'info',
    message: `Ruhepuls erhÃ¶ht: ${data.resting_hr} bpm`
  });
}

return [{
  json: {
    ...data,
    alerts,
    has_alerts: alerts.length > 0
  }
}];
