parameters.jsCode:
// Calculate date for GMI API
const lastSync = $input.item.json.last_sync_at;
const now = new Date();

// Default to 30 days ago if no last sync
let fromDate;
if (lastSync) {
  fromDate = new Date(lastSync);
  // Go back 1 day to catch any late documents
  fromDate.setDate(fromDate.getDate() - 1);
} else {
  fromDate = new Date();
  fromDate.setDate(fromDate.getDate() - 30);
}

// Format as ISO string for GMI API
return {
  json: {
    created_since: fromDate.toISOString(),
    last_sync: lastSync || null
  }
};

// Extract documents array from GMI response
const response = $input.item.json;

// GMI API may return data in different formats
let documents = response.records || response.documents || response.data || [];

if (!Array.isArray(documents)) {
  documents = [documents];
}

return documents.map(doc => ({ json: doc }));

// Transform GMI document to Supabase invoice format
const doc = $input.item.json;

// Parse amount - GMI may provide it in different formats
const parseAmount = (val) => {
  if (!val) return 0;
  if (typeof val === 'number') return val;
  // Handle German format: 1.234,56 -> 1234.56
  return parseFloat(String(val).replace(/\./g, '').replace(',', '.')) || 0;
};

// Parse date
const parseDate = (val) => {
  if (!val) return null;
  const date = new Date(val);
  return isNaN(date.getTime()) ? null : date.toISOString().split('T')[0];
};

// Determine invoice type
const getInvoiceType = (doc) => {
  const docType = (doc.document_type || doc.type || '').toLowerCase();
  if (docType.includes('eingang') || docType.includes('incoming') || docType.includes('lieferant')) {
    return 'eingang';
  }
  if (docType.includes('ausgang') || docType.includes('outgoing') || docType.includes('kunde')) {
    return 'ausgang';
  }
  return 'sonstige';
};

// Determine payment status
const getPaymentStatus = (doc) => {
  const status = (doc.payment_status || doc.status || '').toLowerCase();
  if (status.includes('paid') || status.includes('bezahlt')) return 'bezahlt';
  if (status.includes('partial') || status.includes('teil')) return 'teilweise_bezahlt';
  if (status.includes('overdue') || status.includes('überfällig')) return 'überfällig';
  return 'offen';
};

return {
  json: {
    gmi_document_id: doc.id || doc.document_id,
    invoice_number: doc.invoice_number || doc.document_number || null,
    invoice_date: parseDate(doc.invoice_date || doc.document_date || doc.date),
    due_date: parseDate(doc.due_date || doc.payment_due_date),
    gross_amount: parseAmount(doc.gross_amount || doc.total_gross || doc.amount),
    net_amount: parseAmount(doc.net_amount || doc.total_net),
    tax_amount: parseAmount(doc.tax_amount || doc.vat_amount),
    currency: doc.currency || 'EUR',
    vendor_name: doc.sender_name || doc.supplier_name || doc.vendor || null,
    vendor_id: doc.sender_id || doc.supplier_id || null,
    customer_name: doc.recipient_name || doc.customer_name || null,
    invoice_type: getInvoiceType(doc),
    payment_status: getPaymentStatus(doc),
    category: doc.category || doc.expense_category || null,
    tags: doc.tags || [],
    pdf_url: doc.file_url || doc.download_url || doc.pdf_url || null,
    thumbnail_url: doc.thumbnail_url || doc.preview_url || null,
    ocr_text: doc.ocr_text || doc.extracted_text || null,
    notes: doc.notes || doc.description || null,
    created_at_gmi: doc.created_at || doc.upload_date,
    is_new: true
  }
};

